import React from 'react';
import Contador from './Contador';

const ItemDetail = ({ item }) => {
    const discount = item.price - (item.price * item.descuento) / 100;
    return (
        <div className="container-page container-detail">
            <img src={item.img} alt="detail" />

            <article>
                <h2>{item.title}</h2>
                <section>
                    <h3>$ {discount}.-</h3>
                    <h4>$ {item.price}.-</h4>
                </section>
                <span className="info-span">
                    Los hongos contienen una amplia gama de propiedades medicinales y gourmets.
                </span>
                <Contador stock={20} />
            </article>
        </div>
    );
};

export default ItemDetail;
